def area_of_circle(radius):
    b=3.14*radius*radius
    return b
r=area_of_circle(5)
print("Area of circle is:",r)
def circumference_of_circle(radius):
    c=2*3.14*radius
    return c
r=circumference_of_circle(5)
print("Circumference of circle is:",r)
def diameter_of_circle(radius):
    d=2*radius
    return d
